import { withFormik } from 'formik';
import * as Yup from 'yup';
import { ButtonGroup, ListGroup, Form, Spinner, Row, Col, Image, Card, Button, InputGroup } from 'react-bootstrap';

function VoucherForm(props) {

    return (
        <Form>

            <Form.Group className="mt-4">
                <InputGroup>
                    <InputGroup.Prepend>
                        <InputGroup.Text>@</InputGroup.Text>
                    </InputGroup.Prepend>
                    <Form.Control 
                        value={props.values.code}
                        onChange={e => props.setFieldValue('code', e.target.value)}
                        placeholder="Cupom"
                    />
                </InputGroup>
                {props.touched.code && props.errors.code && 
                    <Form.Control.Feedback type="invalid">{props.errors.code}</Form.Control.Feedback>
                }
            </Form.Group>

        </Form>
    )

}

export default withFormik({
  mapPropsToValues: props => ({ 
      code: '',
    }),

  validationSchema: Yup.object().shape({
    code: Yup.string().required('Informe o código do cupom'),
  }),

  handleSubmit: async (values, { setSubmitting, setErrors, props, setStatus }) => {
      try {
        setSubmitting(true);
        const result = await post(`/cart/addVoucher`, values);

        if (result.success) {
            setSubmitting(false);
            setStatus({ success: true, data: result });
        } else {
            setSubmitting(false);
            setErrors({ error: 'E-mail ou senha inválidos' });
        }

      } catch(error) {
          setErrors({ error: 'E-mail ou senha inválidos' });
      }
  },

})(VoucherForm);
