import { Modal, Button, ListGroup } from 'react-bootstrap';
import currency from '../../../utils/currency';

export default function(props) {

  return (
      <Modal show={props.showDetails} onHide={props.closeDetails}>
          <Modal.Header closeButton>
            <Modal.Title>
              {props.cartProduct.product.name}
              <span className="text-muted">{props.cartProduct.option && props.cartProduct.option.name}</span>
            </Modal.Title>
          </Modal.Header>

          <Modal.Body>
            <ListGroup className="mb-4">

              {props.cartProduct.groups.map(group => 
                    <Col xs={6} key={group.id}>
                        <ListGroup className="mb-2 list-groups">
                            <ListGroup.Item className="p-0 m-0 border-0 group-option">
                                {group.name}
                            </ListGroup.Item>

                            {group.options.map(groupOption => 
                                <ListGroup.Item className="p-0 m-0 border-0 group-option-name" key={groupOption.id}>
                                    - {groupOption.option.name} + 
                                    <span className="text-muted">{currency(groupOption.option.price)}</span>
                                </ListGroup.Item>
                            )}
                        </ListGroup>
                    </Col>
                )}


              {/* {props.cartProduct.options.map(cartOption =>
                  <ListGroup.Item 
                      key={cartOption.id} 
                      className="d-flex justify-content-between align-items-center"
                  >
                      <div>
                          {cartOption.option.name}
                          <span className="text-muted"> + {currency(cartOption.option.price)}</span>
                      </div>

                  </ListGroup.Item>
              )} */}
            </ListGroup>
          </Modal.Body>

          <Modal.Footer>
            <Button variant="primary" onClick={props.closeDetails}>
              Fechar
            </Button>
          </Modal.Footer>
      </Modal>   
    )

}
