import React, { useState, useEffect } from 'react';
import { ButtonGroup, ListGroup, Form, Spinner, Row, Col, Image, Card, Button } from 'react-bootstrap';
import { useSelector, useDispatch } from 'react-redux';
import Link from 'next/link';

import currency from '../../utils/currency';
import { showCart, loadCart } from '../../store/actions/cart';
import { get, patch, httpDelete } from '../../services/http';
import './styles.scss';

function Cart() {
    const [addresses, setAddresses] = useState([]);
    const [addressSelected, setAddressSelected] = useState({});

    const dispatch = useDispatch();
    const { isLogged } = useSelector(state => state.auth);
    const { show, cart, loading } = useSelector(state => state.cart);

    useEffect(() => {
        async function getAddresses() {
            const result = await get('/myAddress');

            if (!addressSelected.id) {
                const addressDefault = result.find(item => item.is_default);
                setAddressSelected(addressDefault);
            }

            setAddresses(result);
        } 

        if (isLogged) getAddresses();
    }, [isLogged]);

    async function deleteItem(cartProduct) {
        await httpDelete(`/cart/${cartProduct.cart_id}/product/${cartProduct.id}`);
        dispatch(loadCart());
    }

    async function setAddress(address) {
        setAddressSelected(address);
        await patch(`/myAddress/set-default/${address.id}`, address);
        dispatch(loadCart());
    }

    if (!show) return <div />;

    return (
        <div id="block-cart" className="shadow">
            <div className="cart-header p-2 d-flex align-items-center border-bottom shadow justify-content-between">

                <div className="d-flex align-items-center">
                    <h4 className="m-0 mr-1 text-light text-primary">
                        Sua sacola
                    </h4>
                    ({cart.products.length}) produto{cart.products <= 1 ? '' : 's'}
                </div>

                <button className="btn btn-light rounded-circle"
                    onClick={() => {
                        dispatch(showCart(false));
                    }}
                >
                    <i className="fas fa-times"></i>
                </button>
            </div>

            <div className="body p-4 border-bottom">
                {cart.products.map(cartProduct =>
                    <Card className="rounded position-relative mb-4 pt-1 pb-2 item border-0" key={cartProduct.id}>
                        <Row key={cartProduct.id}>
                            <Col xs={4}>
                                <Image src={cartProduct.product.image_url} className="img-fluid" />
                            </Col>
                            <Col xs={8}>
                                <h6 className="text-primary mb-0">{cartProduct.product.name}</h6>
                                <b className="group-name text-secondary mb-0">Tamanho</b> <br />
                                {cartProduct.option && <span className="group-value">{cartProduct.option.name}</span>}

                                {cartProduct.groups.map(group => 
                                    <ListGroup className="mb-4">
                                         <ListGroup.Item className="p-0 m-0 border-0">{group.name}</ListGroup.Item>

                                         {group.options.map(groupOption => 
                                            <ListGroup.Item>{groupOption.option.name}</ListGroup.Item>
                                        )}
                                    </ListGroup>
                                )}

                            </Col>

                            <Col></Col>

                            <Button 
                                variant="danger"
                                size="sm"
                                className="rounded-circle btn-trash"
                                onClick={() => {
                                    deleteItem(cartProduct);
                                }}
                            >
                                <i className="fas fa-trash"></i>
                            </Button>
                        </Row>
                    </Card>
                )}
            </div>

            <div className="footer w-100 pt-3">

                <div className="addresses ml-3 mr-3 mb-4">
                    {addresses.length > 1 && addresses.map(address => 
                        <Form.Group key={address.id} controlId={`address-${address.id}`} className="mb-0">
                            <Form.Check 
                                type="radio" 
                                value={address.id}
                                checked={addressSelected.id === address.id}
                                label={`
                                    ${address.street}, ${address.number} - 
                                    ${address.neighborhood.name} 
                                `} 
                                onChange={() => setAddress(address)}
                            />
                        </Form.Group>
                    )}                    
                </div>

                <ListGroup className="shadow ml-3 mr-3 mb-4 border-0">
                    <ListGroup.Item className="d-flex justify-content-between">
                        <div>
                            Total de Items 
                        </div>
                        <div>
                            {loading ? <Spinner animation="border" /> : currency(cart.total_products)}
                        </div>
                    </ListGroup.Item>
                    <ListGroup.Item className="d-flex justify-content-between">
                        <div>
                            Taxa de Entrega 
                        </div>
                        <div>
                            {loading ? <Spinner animation="border" /> : currency(cart.shipping)}
                        </div>
                    </ListGroup.Item>
                    <ListGroup.Item className="d-flex justify-content-between">
                        <div>
                            Valor total 
                        </div>
                        <div>
                            {loading ? <Spinner animation="border" /> : currency(cart.total_price)}
                        </div>
                    </ListGroup.Item>
                </ListGroup>

                <div className="d-flex flex-column">
                    <ButtonGroup>
                        <div className="btn btn-light btn-lg rounded-0">
                            Total: {currency(555)}
                        </div>
                        <Link href="/checkout">
                            <a className="btn btn-success btn-lg rounded-0">
                                <i className="fas fa-shopping-cart"></i>
                                <span>Finalizar Pedido</span>
                            </a>
                        </Link>
                    </ButtonGroup>
                </div>
            </div>
        </div>
    );
}

export default Cart;
