import React from 'react';
import { ButtonGroup, ListGroup } from 'react-bootstrap';
import { useSelector } from 'react-redux';
import Link from 'next/link';

import currency from '../../utils/currency';
import './styles.scss';

export default function Cart() {

    const { show } = useSelector(state => state.cart);

    if (!show) return <div />;

    return (
        <div id="block-cart" className="shadow">
            <div className="cart-header p-2 d-flex align-items-center border-bottom shadow justify-content-between">

                <h4 className="m-0 text-light text-primary">Sua sacola</h4>

                <button class="btn btn-light rounded-circle">
                    <i className="fas fa-times"></i>
                </button>
            </div>

            <div className="body"></div>

            <div className="footer w-100 pt-3 pb-3">

                <ListGroup className="shadow pl-3 pr-3 mb-5 border-0">
                    <ListGroup.Item className="d-flex justify-content-between border-0">Total de Items {currency(11)}</ListGroup.Item>
                    <ListGroup.Item className="d-flex justify-content-between border-0">Taxa de Entrega {currency(11)}</ListGroup.Item>
                    <ListGroup.Item className="d-flex justify-content-between border-0">Valor total {currency(11)}</ListGroup.Item>
                </ListGroup>

                <div className="d-flex flex-column">
                    <ButtonGroup>
                        <div className="btn btn-light btn-lg rounded-0">
                            Total: {currency(555)}
                        </div>
                        <Link href="/checkout">
                            <a className="btn btn-success btn-lg rounded-0">
                                <i className="fas fa-shopping-cart"></i>
                                <span>Finalizar Pedido</span>
                            </a>
                        </Link>
                    </ButtonGroup>
                </div>
            </div>
        </div>
    );
}
