import { Carousel } from "react-bootstrap";

export default function({ images }) {
    return (
        <Carousel indicators={false} interval={2000}>
            <Carousel.Item>
                <Image src={product.image_url} />
            </Carousel.Item>

            {images.map(image => 
                <Carousel.Item key={image.id}>
                    <img
                        className="d-block w-100"
                        src={image.url}
                        alt={image.url}
                    />
                </Carousel.Item>
            )}

        </Carousel>        
    );
}
