import { useEffect, useState } from 'react';
import Link from 'next/link';
import { Table, Spinner, Alert } from 'react-bootstrap';

import Layout from '../../../components/Layouts/Customer';

import { get } from '../../../services/http';
import Page from '../../../components/Page';

const MyAddresses = (props) => {
    const { addresses } = props
    const [loading, setLoading] = useState(true);

    // useEffect(() => {
    //   async function getData() {
    //       const result = await get('/myAddress');
    //       setAddresses(result);
    //       setLoading(false);
    //   }

    //   getData();
    // }, []);

    console.log(props, 'endereco')

    const pageTitle = 'Meus Endereços';

    const crumbs = [
      { name: 'Minha Conta', link: '/account' },
      { name: pageTitle },
    ];

    if (!addresses) return <div />;

    return (
      <Page title={pageTitle} crumbs={crumbs}>
        <Layout pageTitle={pageTitle}>

            {/* {loading && <Spinner /> } */}
            {/* {addresses.length <= 0 && <Alert>Sem Registros</Alert>} */}

            <div className="pt-4 pb-4 text-right">
              <Link href="/account/addresses/create">
                <a className="btn btn-sm btn-primary">
                  Cadastrar Endereço
                </a>
              </Link>
            </div>

            {/* {!loading && */}
              <Table striped bordered hover>
                <thead>
                  <tr>
                    <th>Cidade</th>
                    <th>UF</th>
                    <th>Rua</th>
                    <th>Número</th>
                    <th>Bairro</th>
                    <th></th>
                  </tr>
                </thead>
                <tbody>
                  {addresses.map(address =>
                    <tr key={address.id}>
                      <td>{address.city.name}</td>
                      <td>{address.city.state.name}</td>
                      <td>{address.number}</td>
                      <td>{address.street}</td>
                      <td>{address.neighborhood.name}</td>
                      <td>
                          <Link href={`/account/addresses/${address.id}`}>
                            <a className="btn btn-primary btn-sm">
                                <i className="fas fa-edit"></i> 
                            </a>
                          </Link>
                      </td>
                    </tr>
                  )}
                </tbody>
              </Table>
            {/* } */}
        </Layout>
      </Page>
    );

}

MyAddresses.getInitialProps = async ctx => {
  console.log(ctx )
  const addresses = await get('/myAddress')
  return { addresses }
}

export default MyAddresses;
