import React, { useState, useEffect } from 'react';
import { ButtonGroup, ListGroup } from 'react-bootstrap';
import { useSelector, useDispatch } from 'react-redux';
import Link from 'next/link';

import currency from '../../utils/currency';
import { showCart } from '../../store/actions/cart';
import { get } from '../../services/http';
import './styles.scss';

function Cart() {
    const [addresses, setAddresses] = useState([]);
    const dispatch = useDispatch();
    const { isLogged } = useSelector(state => state.auth);
    const { show, cart } = useSelector(state => state.cart);

    useEffect(() => {
        async function getAddresses() {
            const result = await get('/myAddress');
            setAddresses(result);
        } 

        if (isLogged) getAddresses();
    }, [isLogged]);

    if (!show) return <div />;

    console.log(addresses)

    return (
        <div id="block-cart" className="shadow">
            <div className="cart-header p-2 d-flex align-items-center border-bottom shadow justify-content-between">

                <h4 className="m-0 text-light text-primary">Sua sacola</h4>

                <button className="btn btn-light rounded-circle"
                    onClick={() => {
                        dispatch(showCart(false));
                    }}
                >
                    <i className="fas fa-times"></i>
                </button>
            </div>

            <div className="body"></div>

            <div className="footer w-100 pt-3">

                <div className="addresses">
                    {addresses.map(address => 
                        <Form.Group key={address.id} controlId={`address-${address.id}`}>
                            <Form.Check 
                                type="radio" 
                                value={address.id}
                                checked={data.user_address_id === address.id}
                                label={`
                                    ${address.street}, ${address.number} - 
                                    ${address.neighborhood.name} 
                                    ${address.city.name} - ${address.city.state.uf}, 
                                    ${address.cep ? address.cep : ''}
                                `} 
                                onChange={(e) => {
                                    data.user_address_id = parseInt(e.target.value);
                                    setError({ ...error, user_address_id: null })
                                    setData(() => ({ ...data, ...data }));
                                }}
                            />

                            <strong>Entrega:</strong> {currency(address.neighborhood.price)} <br/>
                            {address.neighborhood.description}

                            <hr />
                        </Form.Group>
                    )}                    
                </div>

                <ListGroup className="shadow ml-3 mr-3 mb-4 border-0">
                    <ListGroup.Item className="d-flex justify-content-between">
                        <div>
                            Total de Items 
                        </div>
                        <div>
                            {currency(11)}
                        </div>
                    </ListGroup.Item>
                    <ListGroup.Item className="d-flex justify-content-between">
                        <div>
                            Taxa de Entrega 
                        </div>
                        <div>
                            {currency(11)}
                        </div>
                    </ListGroup.Item>
                    <ListGroup.Item className="d-flex justify-content-between">
                        <div>
                            Valor total 
                        </div>
                        <div>
                            {currency(11)}
                        </div>
                    </ListGroup.Item>
                </ListGroup>

                <div className="d-flex flex-column">
                    <ButtonGroup>
                        <div className="btn btn-light btn-lg rounded-0">
                            Total: {currency(555)}
                        </div>
                        <Link href="/checkout">
                            <a className="btn btn-success btn-lg rounded-0">
                                <i className="fas fa-shopping-cart"></i>
                                <span>Finalizar Pedido</span>
                            </a>
                        </Link>
                    </ButtonGroup>
                </div>
            </div>
        </div>
    );
}

export default Cart;
