import React from 'react';
import { ButtonGroup, ListGroup } from 'react-bootstrap';
import { useSelector, useDispatch } from 'react-redux';
import Link from 'next/link';

import currency from '../../utils/currency';
import './styles.scss';
import { showCart } from '../../store/actions/cart';

export default function Cart() {

    const dispatch = useDispatch();
    const { show } = useSelector(state => state.cart);

    if (!show) return <div />;

    return (
        <div id="block-cart" className="shadow">
            <div className="cart-header p-2 d-flex align-items-center border-bottom shadow justify-content-between">

                <h4 className="m-0 text-light text-primary">Sua sacola</h4>

                <button class="btn btn-light rounded-circle"
                    onClick={() => {
                        dispatch(showCart(false));
                    }}
                >
                    <i className="fas fa-times"></i>
                </button>
            </div>

            <div className="body"></div>

            <div className="footer w-100 pt-3">

                <ListGroup className="shadow ml-3 mr-3 mb-3 border-0">
                    <ListGroup.Item className="d-flex justify-content-between">
                        <div>
                            Total de Items 
                        </div>
                        <div>
                            {currency(11)}
                        </div>
                    </ListGroup.Item>
                    <ListGroup.Item className="d-flex justify-content-between">
                        <div>
                            Taxa de Entrega 
                        </div>
                        <div>
                            {currency(11)}
                        </div>
                    </ListGroup.Item>
                    <ListGroup.Item className="d-flex justify-content-between">
                        <div>
                            Valor total 
                        </div>
                        <div>
                            {currency(11)}
                        </div>
                    </ListGroup.Item>
                </ListGroup>

                <div className="d-flex flex-column">
                    <ButtonGroup>
                        <div className="btn btn-light btn-lg rounded-0">
                            Total: {currency(555)}
                        </div>
                        <Link href="/checkout">
                            <a className="btn btn-success btn-lg rounded-0">
                                <i className="fas fa-shopping-cart"></i>
                                <span>Finalizar Pedido</span>
                            </a>
                        </Link>
                    </ButtonGroup>
                </div>
            </div>
        </div>
    );
}
