import { Modal, Row, Col, ListGroup } from 'react-bootstrap';

import Carousel from './Carousel';
import currency from '../../../utils/currency';

import './styles.scss';

function CartProductDetails({ cartProduct, onHide, show, onUpdate }) {
    if (!cartProduct || !cartProduct.product) return <div />;

    return (
      <Modal
        size="lg"
        aria-labelledby="contained-modal-title-vcenter"
        show={show}
        onHide={onHide}
      >
        <Modal.Header closeButton>
          <Modal.Title id="contained-modal-title-vcenter">
            {cartProduct.product.name}
          </Modal.Title>
        </Modal.Header>
        <Modal.Body>

            <Row className="productShow">
                <Col xs={6}>
                    <Carousel images={cartProduct.product.images} />
                </Col>

                <Col className="pt-4">
                    <h1 className="name">{cartProduct.product.name}</h1>
                    <p>{cartProduct.product.description}</p>
                    <h1 className="price">{currency(cartProduct.total_price)}</h1>

                    {cartProduct.product.options && 
                        <ListGroup className="options mt-4" horizontal>
                            {cartProduct.product.options.map(option =>
                                <ListGroup.Item 
                                    key={option.id} 
                                    active={cartProduct.option.id === option.id}
                                >
                                    {option.name}
                                </ListGroup.Item>
                            )}
                        </ListGroup>
                    }

                    <div className="mt-4 groups">
                        <ListGroup className="mb-4">
                            {cartProduct.options.map(cartOption =>
                                <ListGroup.Item 
                                    key={cartOption.id} 
                                    className="d-flex justify-content-between align-items-center"
                                >
                                    <div className="d-flex justify-content-between">
                                        <div>{cartOption.option.name}</div>
                                        <div className="float-right">
                                            <span className="text-muted"> + {currency(cartOption.option.price)}</span>
                                        </div>
                                    </div>
                                </ListGroup.Item>
                            )}
                        </ListGroup>
                    </div>
                </Col>

            </Row>

        </Modal.Body>
      </Modal>
    );
  }

  export default CartProductDetails;
