import { useEffect, useState } from 'react';
import Link from 'next/link';
import { useSelector, useDispatch } from 'react-redux';
import { Container, Table, Button, Spinner } from 'react-bootstrap';
import { useRouter } from 'next/router';

import PageTitle from '../../components/PageTitle';
import Breadcrumb from '../../components/Breadcrumb';

import Checkout from './Checkout';
import CartProductDetails from './Product/Details';

import currency from '../../utils/currency';
import { patch, httpDelete } from '../../services/http';
import { addCrumb, clearCrumbs } from '../../store/actions/breadcrumb';
import { loadCart } from '../../store/actions/cart';

const PageCart = (props) => () => {
  const [cartProductActive, setCartProductActive] = useState({});
  const [update, setUpdate] = useState({});
  const { cart } = useSelector(state => state.cart);
  const { setting, loaded } = useSelector(state => state.setting);
  const dispatch = useDispatch();
  const router = useRouter();

  const [openCheckout, setOpenCheckout] = useState(false);

  useEffect(() => {
    dispatch(clearCrumbs());
    dispatch(addCrumb('Meu Carrinho'));
  }, []);

  async function incrementQuantity(cartProduct) {
      cartProduct.quantity += 1;

      await patch(`/cart/${cart.id}/product/${cartProduct.id}`, { quantity: cartProduct.quantity });
      dispatch(loadCart());
  }

  async function decrementQuantity(cartProduct) {
      cartProduct.quantity -= 1;

      if (cartProduct.quantity <= 0) {
        cartProduct.quantity = 1;
      }

      await patch(`/cart/${cart.id}/product/${cartProduct.id}`, { quantity: cartProduct.quantity });
      dispatch(loadCart());
  }

  async function deleteItem(cartProduct) {
    await httpDelete(`/cart/${cart.id}/product/${cartProduct.id}`);
    dispatch(loadCart());
  }

  return (
      <Container>
          <Breadcrumb />
          <PageTitle title="Meu Carrinho" />

          <Checkout 
            show={openCheckout && cart.products.length > 0}
            onHide={() => setOpenCheckout()}
          />

          <CartProductDetails 
            cartProduct={cartProductActive}
            show={cartProductActive.id > 0}
            onHide={() => setCartProductActive({})}
            onUpdate={(data) => {
              updateCartProduct(data);
              setUpdate({data});
            }}
          />

          {!cart.id && <Spinner /> }

          {cart.id &&
            <Table striped bordered hover>
              <thead>
                <tr>
                  <th>Produto</th>
                  <th>Preço</th>
                  <th>Quantidade</th>
                  <th>Total</th>
                  <th></th>
                </tr>
              </thead>
              <tbody>
                {cart.products.map(cartProduct =>
                  <tr key={cartProduct.id}>
                    <td>
                      {cartProduct.product.name} 
                      {cartProduct.option && cartProduct.option.name}
                    </td>
                    <td>{currency(cartProduct.price_unity)}</td>
                    <td>{currency(cartProduct.total_price)}</td>
                    <td className="d-flex">
                        <Button className="btn-quantity left btn-sm" onClick={() => {
                            decrementQuantity(cartProduct);
                        }}>
                            <i className="fas fa-minus-circle"></i>
                        </Button>
                        <div className="quantity btn btn-sm bg-light border-sm pl-3 pr-3 rounded-0">
                            {cartProduct.quantity}
                        </div>
                        <Button className="btn-quantity right btn-sm" onClick={() => {
                            incrementQuantity(cartProduct);
                        }}>
                            <i className="fas fa-plus-circle"></i>
                        </Button>
                    </td>
                    <td>
                      <Button size="sm" onClick={() => setCartProductActive(cartProduct)}>
                        <i className="fas fa-eye"></i>
                      </Button>
                      <Button size="sm" variant="danger" className="ml-2" onClick={() => deleteItem(cartProduct)}>
                        <i className="fas fa-trash"></i>
                      </Button>
                    </td>
                  </tr>
               )}
               <tr>
                <td></td>
                <td></td>
                <td></td>
                <td></td>
                <td>
                  <strong>Total:</strong>
                  {currency(cart.total_price)}
                </td>
               </tr>
              </tbody>
            </Table>
          }

          <div className="mt-5 d-flex justify-content-between">
            <Link href="/">
              <a className="btn btn-light">
                <i className="fas fa-chevron-left"></i> 
                Continuar Comprando  
              </a>
            </Link>

            {setting.store_status && setting.store_status.value !== "0" &&
              <button className="btn btn-primary" 
                  onClick={() => 
                  setOpenCheckout(true)} 
                  disabled={cart.products.length <= 0}
              >
                Finalizar Pedido  
                <i className="fas fa-chevron-right"></i> 
              </button>
            }
          </div>


      </Container>    
  );

}

// PageCart.getInitialProps = () => {
//   return {}
// }

export default PageCart;
