import { useState, useEffect } from 'react';
import { useRouter } from 'next/router'
import { useDispatch } from 'react-redux';
import { Form, Modal, Button, Row, Col, ListGroup, Alert } from 'react-bootstrap';
import { toast } from 'react-toastify';

import Spinner from '../../../components/Spinner';

import { get, post } from '../../../services/http';
import { loadCart } from '../../../store/actions/cart';
import currency from '../../../utils/currency';

import './styles.scss';

function Checkout({ onHide, show }) {
    const [paymentMethods, setPaymentMethods] = useState([]);
    const [addresses, setAddresses] = useState([]);
    const [loading, setLoading] = useState(true);
    const [data, setData] = useState({});
    const [error, setError] = useState({});

    const dispatch = useDispatch(loadCart());

    const router = useRouter();

    console.log(userAddresses)

    useEffect(() => {
        async function getAddresses() {
            const result = await get('/myAddress');
            setAddresses(result);
        } 

        getAddresses();
    }, []);

    useEffect(() => {
        async function getPaymentMethods() {
            const result = await get('/paymentMethod');
            setPaymentMethods(result);

            if (!data.payment_method_id) {
                data.payment_method_id = result[0].id;
                setData(() => ({ ...data, ...data }));
            }

            setLoading(false);
        } 

        getPaymentMethods();
    }, []);

    async function processCheckout() {
        setError({});
        setLoading(true);

        if (!data.user_address_id) {
            return setError({ user_address_id: 'Selecione um endereÃ§o para entrega.' });
        }

        if (!data.payment_method_id) {
            return setError({ payment_method_id: 'Selecione a forma de pagamento.' });
        }

        const result = await post('/checkout', data);
        setLoading(true);

        toast.success('Seu pedido fopi enviado com sucesso!', {
            position: toast.POSITION.BOTTOM_CENTER
        });

        router.push(`/order/${result.id}`);
    }

    return (
      <Modal
        size="lg"
        aria-labelledby="contained-modal-title-vcenter"
        show={show}
        onHide={onHide}
      >
        <Modal.Header closeButton>
          <Modal.Title id="contained-modal-title-vcenter">
            Finalizar Pedido
          </Modal.Title>
        </Modal.Header>
        <Modal.Body>

            <div className="mb-3">
                <h6>Selecione o endereÃ§o de entrega</h6>
                {error.user_address_id && <Alert variant="danger">{error.user_address_id}</Alert>}

                {addresses.map(address => 
                    <Form.Group key={address.id} controlId={`address-${address.id}`}>
                        <Form.Check 
                            type="radio" 
                            value={address.id}
                            checked={data.user_address_id === address.id}
                            label={`
                                ${address.street}, ${address.number} - 
                                ${address.neighborhood.name} 
                                ${address.city.name} - ${address.city.state.uf}, 
                                ${address.cep ? address.cep : ''}
                            `} 
                            onChange={(e) => {
                                data.user_address_id = parseInt(e.target.value);
                                setError({ ...error, user_address_id: null })
                                setData(() => ({ ...data, ...data }));
                            }}
                        />

                        <strong>Entrega:</strong> {currency(address.neighborhood.price)} <br/>
                        {address.neighborhood.description}

                        <hr />
                    </Form.Group>
                )}
            </div>

            {paymentMethods && 
                <div>
                    <h6>Selecione o mÃ©todo de Pagamento</h6>
                    <ListGroup className="options mt-4" horizontal>
                        {paymentMethods.map(paymentMethod =>
                            <ListGroup.Item 
                                key={paymentMethod.id} 
                                active={data.payment_method_id === paymentMethod.id}
                                onClick={() => {
                                    data.payment_method_id = paymentMethod.id;
                                    setData(() => ({ ...data, ...data }));
                                }}
                            >
                                {paymentMethod.name}
                            </ListGroup.Item>
                        )}
                    </ListGroup>
                </div>
            }

            <Form.Group controlId="observation" className="mt-3">
                <Form.Label>ObservaÃ§Ã£o</Form.Label>
                <Form.Control as="textarea" rows="3" onChange={(e) => {
                    data.observation = e.target.value;
                    setData(() => ({ ...data, ...data }));
                }} />
            </Form.Group>

        </Modal.Body>
        <Modal.Footer className="d-flex justify-content-between">
            <Button variant="secondary">
                Cancelar
            </Button>
            <Button variant="primary" onClick={processCheckout} disabled={loading}>
                Confirmar Pedido
                {loading && <Spinner />}
            </Button>
        </Modal.Footer>
      </Modal>
    );
}

Checkout.mapInitialProps = async () => {
    const userAddresses = await get('/myAddress');

    return {
        userAddresses
    }
}

export default Checkout;
