import React from 'react';
import { ButtonGroup } from 'react-bootstrap';
import { useSelector } from 'react-redux';
import Link from 'next/link';

import currency from '../../utils/currency';
import './styles.scss';

export default function Cart() {

    const { show } = useSelector(state => state.cart);

    if (!show) return <div />;

    return (
        <div id="block-cart" className="shadow">
            <div className="header p-2 d-flex align-items-center border-bottom shadow">
                <button class="btn btn-light rounded-circle">
                    <i className="fas fa-times"></i>
                </button>

                <h4 className="m-0 text-light text-primary ml-1">Sua sacola</h4>
            </div>

            <div className="body"></div>

            <div className="footer w-100 shadow border-top">
                <div className="d-flex flex-column">
                    <ButtonGroup>
                        <div className="btn btn-light btn-lg rounded-0">
                            Total: {currency(555)}
                        </div>
                        <Link href="/checkout">
                            <a className="btn btn-success btn-lg rounded-0">
                                <i className="fas fa-shopping-cart"></i>
                                <span>Finalizar Pedido</span>
                            </a>
                        </Link>
                    </ButtonGroup>
                </div>
            </div>
        </div>
    );
}
